import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';

export default function SignIn({openRegModal}){



    return (

            <div className='sign_in_modal' onClick={openRegModal}>
                <div className='sign_in_modal_box'>
                    <h1>Sign in</h1>
                    <Formik
                        initialValues={{ email: '', name: '', phone: '' }}
                        validate={values => {
                            const errors = {};
                            if (!values.email) {
                                errors.email = 'Required';
                            } else if (
                                !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
                            ) {
                                errors.email = 'Invalid email address';
                            }
                            return errors;
                        }}
                        onSubmit={(values, { setSubmitting }) => {
                            setTimeout(() => {
                                alert(JSON.stringify(values, null, 2));
                                setSubmitting(false);
                            }, 400);
                        }}
                    >
                        {({ isSubmitting }) => (
                            <Form className='sign_in_form'>
                                <div className="form_item">
                                    <Field type="text" name="name" placeholder='Your name' />
                                    <ErrorMessage name="name" component="div" />
                                </div>
                                <div className='form_item'>
                                    <Field type="email" name="email" placeholder='example@email.com'/>
                                    <ErrorMessage name="email" component="div" />
                                </div>
                                <div className='form_item'>
                                    <Field type="tel" name="phone" placeholder='380674564545'/>
                                    <ErrorMessage name="email" component="div" />
                                </div>
                                <div className='form_item'>
                                    <Field type="text" name="city" placeholder='Lviv'/>
                                    <ErrorMessage name="city" component="div" />
                                </div>
                                <div className='form_item'>
                                    <Field type="text" name="street" placeholder='Centralna 12'/>
                                    <ErrorMessage name="street" component="div" />
                                </div>
                                <div className='form_item'>
                                    <Field type="text" name="post_code" placeholder='11223'/>
                                    <ErrorMessage name="email" component="div" />
                                </div>


                                <button type="submit" disabled={isSubmitting}>
                                    Submit
                                </button>
                            </Form>
                        )}
                    </Formik>
                </div>
            </div>

    )

}
